#!/bin/bash
# SAGE Environment Activation Script (Minimal Setup)
# Generated by SAGE installer

echo "ðŸ”§ Activating SAGE conda environment..."

# Initialize conda if not already initialized
if ! command -v conda &> /dev/null; then
    echo "âŒ Conda not found in PATH"
    echo "ðŸ’¡ Please ensure conda is installed and in your PATH"
    return 1 2>/dev/null || exit 1
fi

# Initialize conda for this shell session
eval "$(conda shell.bash hook)" 2>/dev/null || {
    echo "ðŸ”§ Initializing conda for bash..."
    conda init bash
    echo "ðŸ“ Conda initialized. Please restart your shell or run 'source ~/.bashrc'"
    echo "ðŸ“ Then run this script again: source ./activate_sage.sh"
    return 1 2>/dev/null || exit 1
}

# Activate the sage environment
conda activate sage

if [ $? -eq 0 ]; then
    echo "âœ… SAGE environment activated successfully!"
    echo "ðŸ“ You are now in the (sage) environment"
    echo "ðŸš€ Test with: python -c \"import sage; print('SAGE ready!')\""
    echo "ðŸ’¡ Type 'conda deactivate' to exit this environment"
    echo ""
else
    echo "âŒ Failed to activate SAGE environment"
    echo "ðŸ’¡ Try: conda env list"
    return 1 2>/dev/null || exit 1
fi
